import React, { useContext } from 'react'
import { Link, NavLink, useHistory } from 'react-router-dom'
import { AuthContext } from '../../auth/AuthContext'
import { types } from '../../types/types';
import logo from '../../static/icono.png';

export const Navbar = () => {

    const { user, dispatch } = useContext( AuthContext );

    const history = useHistory();

    const handleLogout = () => {
        dispatch({
            type: types.logout
        });
        history.replace('/');
    }

    return (
        <nav className="navbar navbar-expand-sm navbar-dark">
            <div className='navbar-brand'>
                <img className='logo-navbar' src={logo} alt='Logo Matias Rodriguez'></img>
            </div>

            <div className="navbar-collapse">
                <div className="navbar-nav ms-auto me-2">
                    <NavLink activeClassName="active" className="nav-item nav-link" exact to="/marvel">
                        Marvel
                    </NavLink>

                    <NavLink activeClassName="active" className="nav-item nav-link"  exact to="/dc">
                        DC
                    </NavLink>
                </div>
            </div>

            {
                user.logged &&
                <div className="navbar-collapse collapse w-100 order-3 dual-collapse2" onClick = { handleLogout } >
                    <ul className="navbar-nav ms-auto">
                        <NavLink 
                            activeClassName="active"
                            className="nav-item nav-link" 
                            exact
                            to="/login"
                        >
                            Logout
                        </NavLink>
                    </ul>
                </div>
            }
        </nav>
    )
}
