import { types } from "../types/types";

const history = useHistory();

export const authReducer = ( state ={}, action ) =>{
    switch ( action.type ) {
        case types.login:
            if ( startLoginEmailPassword(action.payload.username, action.payload.password) ) {
                history.replace('/management/create-blog');
                return{
                    ...action.payload.name,
                    logged: true
                }    
            }
            return{
                logged: false
            }
        case types.login:
            return{
                logged: false
            }
        default:
            return state;
    }
}

const startLoginEmailPassword = (username, password) => {
    if ( username === 'matiasrodriguez' && password === 'probando1234' ) {
        return true;    
    }
    return false;
}
